        date: '2024-11-01'
      },
      {
        company: 'TechFlow',
        achievement: 'Acquired by Microsoft for $120M',
        impact: '4.2x return on investment',
        date: '2024-09-30'
      }
    ]
  },

  // Market Environment Analysis
  market_environment: {
    venture_market_conditions: {
      fundraising_environment: 'Challenging but improving',
      valuation_trends: 'Stabilizing after 2023 correction',
      exit_market: 'IPO window gradually reopening',
      competitive_landscape: 'Increased selectivity among investors'
    },
    sector_performance: {
      ai_ml: {
        performance: 'Outperforming',
        outlook: 'Strong continued demand',
        portfolio_exposure: '35%'
      },
      fintech: {
        performance: 'Mixed',
        outlook: 'Regulatory clarity improving',
        portfolio_exposure: '25%'
      },
      healthtech: {
        performance: 'Steady',
        outlook: 'Positive demographic trends',
        portfolio_exposure: '20%'
      }
    }
  },

  // New Investments This Period
  new_investments: [
    {
      company_name: 'NeuroTech AI',
      investment_amount: 3500000,
      round_size: 12000000,
      stage: 'Series A',
      sector: 'AI/Healthcare',
      investment_thesis: 'Leading AI platform for neurological diagnostics with strong IP portfolio',
      key_metrics: {
        revenue_run_rate: 2400000,
        growth_rate: 180,
        team_size: 32
      }
    },
    {
      company_name: 'GreenFlow Energy',
      investment_amount: 2000000,
