      stage: 'Seed',
      sector: 'Climate Tech',
      investment_thesis: 'Revolutionary battery technology for grid-scale energy storage',
      key_metrics: {
        patents_filed: 12,
        pilot_customers: 3,
        team_size: 18
      }
    }
  ],

  // ESG & Impact Reporting
  esg_reporting: {
    diversity_metrics: {
      female_founders_percentage: 33,
      underrepresented_founders_percentage: 28,
      diverse_board_seats_percentage: 45
    },
    environmental_impact: {
