// Generate Executive Summary
lpCommunication.executive_summary = {
  key_messages: [
    `Fund continues to deliver strong performance with ${lpCommunication.fund_performance.net_irr}% net IRR`,
    `Portfolio companies showing resilience with ${lpCommunication.portfolio_highlights.company_valuations.up_rounds} up rounds this period`,
    `Successful exit of TechFlow delivered 4.2x return, demonstrating value creation capabilities`,
    `New investments in AI and climate tech align with long-term macro trends`
  ],
  performance_highlights: [
    `Net IRR of ${lpCommunication.fund_performance.net_irr}% places fund in top quartile`,
    `${lpCommunication.fund_performance.total_value}M total value represents ${lpCommunication.fund_performance.net_multiple}x net multiple`,
    `Strong portfolio momentum with majority of companies exceeding growth targets`
  ],
  outlook: [
    'Market conditions stabilizing with improved exit environment',
    'Portfolio positioned for multiple exits in next 18-24 months',
    'Continued focus on AI and sustainable technology themes'
  ]
};

// Communication Customization by LP Segment
if (lpSegment === 'institutional') {
  lpCommunication.institutional_focus = {
    regulatory_compliance: 'Full SOX compliance across portfolio',
    risk_management: 'Institutional-grade risk monitoring and reporting',
    governance: 'Independent board oversight and fiduciary standards'
  };
} else if (lpSegment === 'family_office') {
  lpCommunication.family_office_focus = {
    tax_optimization: 'QSBS and tax-efficient structuring maximized',
    impact_alignment: 'ESG integration across investment decisions',
    accessibility: 'Regular informal updates and direct access to GP'
  };
} else if (lpSegment === 'corporate') {
  lpCommunication.corporate_focus = {
    strategic_synergies: 'Portfolio company partnerships with LP businesses',
    innovation_insights: 'Early access to emerging technology trends',
    co_investment_opportunities: 'Strategic co-investment in aligned deals'
  };
}

// Report Generation Instructions
lpCommunication.report_instructions = {
  format: 'Professional PDF with executive summary',
  distribution_list: lpSegment === 'all' ? 'Full LP base' : `${lpSegment} segment only`,
  confidentiality: 'LP Confidential - Not for redistribution',
  follow_up_actions: [
    'Schedule individual LP calls for major investors',
    'Prepare detailed backup materials for questions',
    'Coordinate with portfolio companies for potential LP introductions'
  ]
};

return [{ json: lpCommunication }];
